import "./App.css";
import { products } from "./data";
import { PersianDigits } from "./utils";

{products.map((name) => {
  return (<div>{ name }</div>)
})}

function App() {
  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="container">
        <div className="row">
          <div className="col">
            <h3 className="text-center mb-5">سبد خرید</h3>
          </div>
        </div>
        <div className="row">
          <div className="col">
            <div className="list-group">
              {/* این آیتم را تکرار کنید */}
              <div className="list-group-item">
                <div className="d-flex align-items-center justify-content-between">
                  <img
                    src={products[0].image}
                    alt="رب گوجه فرنگی"
                    className="product-image"
                  />
                  <span>{products.map((name) => {return (<div>{ name }</div>)
})}}</span>
                  {/* با این فانکشن می‌توانید عدد را با فرمت ریالی نمایش دهید */}
                  <span>{PersianDigits (products[0].price)}</span>
                </div>
              </div>
              {/* این آیتم را تکرار کنید */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
